val DIRS3PERSO="bcobucket-telecom-2020" //Bertrand
//val DIRS3PERSO="thomas-trivellato-gdelt2020" //Tom
//val DIRS3PERSO="rob-nob-telecom-gdelt2020" //Roberto

// on télécharges ici trois mois de données et on enlève les fichiers à problème
// pour des tests on peut paramétrer les dates à télécharger via une regex sur les noms des zips
val DATES = "/20200[4-6]"
val sampleDF = filesDF.filter(col("url").rlike(DATES)).filter(!(col("url").contains("/20201214121500"))).cache

val DIR_local = "/mnt/tmp/" //attention à modifier par rapport à l'instal utilisateur

object AwsClient{
    val s3 = new AmazonS3Client(new BasicSessionCredentials(AWS_ID, AWS_KEY,AWS_SESSION_TOKEN ))
}


sampleDF.select("url").repartition(100).foreach( r=> {
            val URL = r.getAs[String](0)
            val fileName = r.getAs[String](0).split("/").last
            //val dir = "projet/download/"
            val localFileName = DIR_local + fileName
            fileDownloader(URL,  localFileName)
            val localFile = new File(localFileName)
            AwsClient.s3.putObject(DIRS3PERSO + "/zips", fileName, localFile )
            localFile.delete()

})
